import React, {useState} from 'react'; import { Link, useNavigate } from 'react-router-dom'; import api from '../api';
export default function Signup(){ const [name,setName]=useState(''); const [email,setEmail]=useState(''); const [password,setPassword]=useState(''); const nav = useNavigate();
const submit = async (e)=>{ e.preventDefault(); try{ await api.post('/api/auth/signup',{name,email,password}); alert('Created'); nav('/login'); }catch(err){ alert(err?.response?.data?.msg || 'Error'); } };
return (<div className="max-w-md mx-auto mt-12"><div className="bg-white rounded-2xl p-8 shadow"><h2 className="text-center text-2xl font-bold text-teal-700 mb-2">Create Account</h2><form onSubmit={submit}><label>Full Name</label><input className="w-full mt-1 mb-4 px-3 py-2 border rounded-md" value={name} onChange={e=>setName(e.target.value)} /><label>Email</label><input className="w-full mt-1 mb-4 px-3 py-2 border rounded-md" value={email} onChange={e=>setEmail(e.target.value)} /><label>Password</label><input type="password" className="w-full mt-1 mb-4 px-3 py-2 border rounded-md" value={password} onChange={e=>setPassword(e.target.value)} /><button className="w-full bg-teal-700 text-white py-2 rounded-md">Sign Up</button></form><p className="mt-4 text-center">Already have an account? <Link to="/login">Sign in</Link></p></div></div>); }
